mapper.update:
UPDATE event			
SET user_id = #{userId}    
    ,event_nm = #{eventNm}   
    ,content = #{content}    
    ,capacity = #{capacity}   
    ,movie_info = #{movieInfo} 
    ,start_dt = #{startDt}   
    ,end_dt = #{endDt}     
    ,location = #{location}   
    ,target_dt = #{targetDt}  
    ,reg_id = #{regId}
WHERE event_seq = #{eventSeq}
