mapper.select:
SELECT T1.*,T2.* FROM ( SELECT B.rnum AS num, B.event_seq AS eventSeq, B.user_id AS userId, B.event_nm AS eventNm, B.content, B.capacity, B.movie_info AS movieInfo, B.location, DECODE(TO_CHAR(SYSDATE,'YYYYMMDD'),TO_CHAR(B.reg_dt,'YYYYMMDD') ,TO_CHAR(B.reg_dt,'HH24:MI') ,TO_CHAR(B.reg_dt,'YYYY-MM-DD')) AS regDt, B.target_dt AS targetDt, B.reg_id AS regId, B.genre FROM ( SELECT ROWNUM rnum, A.* FROM( SELECT * FROM event
ORDER BY reg_dt DESC )A WHERE ROWNUM <= (#{pageSize} *(#{pageNum}-1)+#{pageSize}) )B WHERE B.rnum >= (#{pageSize} *(#{pageNum}-1)+1) )T1 CROSS JOIN (SELECT COUNT(*) totalCnt FROM event
)T2

mapper.update:
UPDATE event			
SET user_id = #{userId,jdbcType=VARCHAR}    
    ,event_nm = #{eventNm,jdbcType=VARCHAR}   
    ,content = #{content,jdbcType=VARCHAR}    
    ,capacity = #{capacity,jdbcType=INTEGER}   
    ,movie_info = #{movieInfo,jdbcType=VARCHAR} 
    ,start_dt = TO_DATE('','YYYY-MM-DD HH24:MI:SS')  
    ,end_dt = TO_DATE('','YYYY-MM-DD HH24:MI:SS')     
    ,location = #{location,jdbcType=VARCHAR}   
    ,target_dt = TO_DATE('','YYYY-MM-DD HH24:MI:SS')  
    ,reg_id = #{regId,jdbcType=VARCHAR}
    ,genre = #{genre,jdbcType=VARCHAR}
WHERE event_seq = #{eventSeq,jdbcType=INTEGER}
